    </div>
    <script defer>
        function calc10(params) {
            const siklik = parseFloat(document.querySelector("#inp10_1_1").value);
            const yeniGr = parseFloat(document.querySelector("#inp10_1_2").value);
            const eskiGr = parseFloat(document.querySelector("#inp10_2_1").value);
            const result = (siklik * yeniGr) / eskiGr;
            document.getElementById("ret10").innerText = result;
        }
        document.getElementById("res10").addEventListener("click", calc10);
    </script>
</body>


