    <div id="f1" class="calc">
        <div id="f11_num">11</div>
        <math xmlns="http://www.w3.org/1998/Math/MathML">
            <mfrac>
                <mrow>
                    <mtext>
                        <input type="number" placeholder="ip nm" id="inp11_1_1">
                    </mtext>
                    <mo>x</mo>
                    <msup>
                        <mfenced>
                            <mtext>
                                <input type="number" placeholder="gr" id="inp11_1_2">
                            </mtext>
                        </mfenced>
                        <mn>2</mn>
                    </msup>
                </mrow>
                <mrow>
                    <msup>
                        <mtext>
                            <input type="number" placeholder="istenen gr" id="inp11_2_1">
                        </mtext>
                        <mn>2</mn>
                    </msup>
                </mrow>
            </mfrac>
        </math>
        <button type="button" id="res11">=</button>
        <div id="ret11"></div>
    </div>
    <script defer>
        document.getElementById("res11").addEventListener("click", () => {
            const ipNm = parseFloat(document.querySelector("#inp11_1_1").value);
            const gr = parseFloat(document.querySelector("#inp11_1_2").value);
            const istenenGr = parseFloat(document.querySelector("#inp11_2_1").value);
            const sonuc = ((ipNm * Math.pow(gr, 2)) / Math.pow(istenenGr, 2)).toFixed(2);
            document.getElementById("ret11").innerText = sonuc;
        });
    </script>
</body>
</html>



