    <div id="f1" class="calc">

        <div id="f12_num">12</div>
        <math xmlns="http://www.w3.org/1998/Math/MathML">
            <msqrt>
                <mfrac>
                    <mrow>
                        <msup>
                            <mrow>
                                <mtext>
                                    <input type="number" placeholder="bilinen s değeri" id="inp12_1_1">
                                </mtext>
                            </mrow>
                            <mn>2</mn>
                        </msup>
                        <mo>&#8290;</mo>
                        <mo>&#8290;</mo>
                        <mo>&#8290;</mo>
                        <mtext>
                            <input type="number" placeholder="yeni nm değeri" id="inp12_2_1">
                        </mtext>
                    </mrow>
                    <mrow>
                        <mtext>
                            <input type="number" placeholder="eski nm değeri" id="inp12_3_1">
                        </mtext>
                    </mrow>
                </mfrac>
            </msqrt>
        </math>
        <button type="button" id="res12">=</button>
        <div id="ret12"></div>

    </div>

    <script defer>
        function calc12(params) {
            const bilinenS = parseFloat(document.querySelector("#inp12_1_1").value);
            const yeniNm = parseFloat(document.querySelector("#inp12_2_1").value);
            const eskiNm = parseFloat(document.querySelector("#inp12_3_1").value);

            const result = Math.sqrt((Math.pow(bilinenS, 2) * yeniNm) / eskiNm);
            document.getElementById("ret12").innerText = result;
        }
        document.getElementById("res12").addEventListener("click", calc12);
    </script>
</body>

</html>
