        <div id="f13_num">13</div>
        <math xmlns="http://www.w3.org/1998/Math/MathML">
            <mfrac>
                <mrow>
                    <mo>(</mo>
                    <mo>(</mo>
                    <mtext>
                        <input type="number" placeholder="n1 değeri" id="inp13_1_1">
                    </mtext>
                    <mo>&times;</mo>
                    <mtext>
                        <input type="number" placeholder="s1 değeri" id="inp13_1_2">
                    </mtext>
