                    <mo>+</mo>
                    <mo>(</mo>
                    <mtext>
                        <input type="number" placeholder="n2 değeri" id="inp13_1_3">
                    </mtext>
                    <mo>&times;</mo>
                    <mtext>
                        <input type="number" placeholder="s2 değeri" id="inp13_1_4">
                    </mtext>
                    <mo>)</mo>
                    <mo>)</mo>
                </mrow>
                <mrow>
                    <mtext>
                        <input type="number" placeholder="s1 değeri" id="inp13_2_1">
                    </mtext>
                    <mo>+</mo>
                    <mtext>
                        <input type="number" placeholder="s2 değeri" id="inp13_2_2">
                    </mtext>
                </mrow>
            </mfrac>
        </math>
        <button type="button" id="res13">=</button>
        <div id="ret13"></div>

    </div>

    <script defer>
        function calc13(params) {
            const n1 = parseFloat(document.querySelector("#inp13_1_1").value);
            const s1 = parseFloat(document.querySelector("#inp13_1_2").value);
            const n2 = parseFloat(document.querySelector("#inp13_1_3").value);
            const s2 = parseFloat(document.querySelector("#inp13_1_4").value);
            const s1_2 = parseFloat(document.querySelector("#inp13_2_1").value);
            const s2_2 = parseFloat(document.querySelector("#inp13_2_2").value);

            const result = (((n1 * s1) + (n2 * s2)) / (s1_2 + s2_2));
            document.getElementById("ret13").innerText = result;
        }
        document.getElementById("res13").addEventListener("click", calc13);
    </script>
</body>
</html>
