        function calc14(params) {
            const cmTAdSquare = parseFloat(document.querySelector("#inp14_1_1").value);
            const yeniNm = parseFloat(document.querySelector("#inp14_1_2").value);
            const eskiNm = parseFloat(document.querySelector("#inp14_2_1").value);

            const result = Math.sqrt((cmTAdSquare ** 2 * yeniNm) / eskiNm);
