    <script defer>
        function calc18(params) {
            const cmTad = parseFloat(document.querySelector("#inp18_1_1").value);
            const te = parseFloat(document.querySelector("#inp18_1_2").value);
            const rpad = parseFloat(document.querySelector("#inp18_1_3").value);
            const hamU = parseFloat(document.querySelector("#inp18_1_4").value);
            const ipNm = parseFloat(document.querySelector("#inp18_2_1").value);
            const rptad = parseFloat(document.querySelector("#inp18_2_2").value);

            const result = (cmTad * te * rpad * hamU) / (ipNm * 1000 * rptad);
            document.getElementById("ret18").innerText = result;
        }
        document.getElementById("res18").addEventListener("click", calc18);
    </script>
