        function calc33(params) {
            const siklik = parseFloat(document.querySelector("#inp33_1_1").value);
            const yeniGr = parseFloat(document.querySelector("#inp33_1_2").value);
            const eskiGr = parseFloat(document.querySelector("#inp33_2_1").value);
            const result = (siklik * yeniGr) / eskiGr;
            document.getElementById("ret33").innerText = result;
        }
        document.getElementById("res33").addEventListener("click", calc33);
    </script>
