        function calc39(params) {
            const c1m2ipu = parseFloat(document.querySelector("#inp_39_1").value);
            const mamulEn = parseFloat(document.querySelector("#inp_39_2").value);
            const ipNm = parseFloat(document.querySelector("#inp_39_3").value);
            const cekim = parseFloat(document.querySelector("#inp_39_4").value);

            const result = ((c1m2ipu * mamulEn * 100) / (ipNm * 2 * (-cekim / 100))).toFixed(3);
            document.getElementById("ret_39").innerText = result;
        }
        document.getElementById("res_39").addEventListener("click", calc39);
