    <script defer>
        function calc48(params) {
            const cmAtki = parseFloat(document.querySelector("#inp48_1_1").value);
            const tEni = parseFloat(document.querySelector("#inp48_1_2").value);
            const cu = parseFloat(document.querySelector("#inp48_1_3").value);
            const ipNm = parseFloat(document.querySelector("#inp48_2_1").value);
            const mamulU = parseFloat(document.querySelector("#inp48_2_2").value);
            const kayip = parseFloat(document.querySelector("#inp48_2_3").value);
            const result = (cmAtki * tEni * cu * 100) / (ipNm * 1000 * mamulU * 100) - mamulU * ((100 - kayip) /100);
            document.getElementById("ret48").innerText = result;
        }
        document.getElementById("res48").addEventListener("click", calc48);
    </script>
