        function calc47(params) {
            const cmAtki = parseFloat(document.querySelector("#inp47_1_1").value);
            const tEni = parseFloat(document.querySelector("#inp47_1_2").value);
            const cu = parseFloat(document.querySelector("#inp47_1_3").value);
            const ipNm = parseFloat(document.querySelector("#inp47_2_1").value);
            const mamulU = parseFloat(document.querySelector("#inp47_2_2").value);
            const kayip = parseFloat(document.querySelector("#inp47_2_3").value);
            const result = (cmAtki * tEni * cu * 100) / (ipNm * 1000 * mamulU * 100) - mamulU * ((100 - kayip) /100);
            document.getElementById("ret47").innerText = result;
        }
