    <script defer>
        function calcCustom(params) {
            const rpTelAd1 = parseFloat(document.querySelector("#inp_custom_1").value);
            const ipNm1 = parseFloat(document.querySelector("#inp_custom_2").value);
            const rpTelAd2 = parseFloat(document.querySelector("#inp_custom_3").value);
            const ipNm2 = parseFloat(document.querySelector("#inp_custom_4").value);
            const rpTelAd3 = parseFloat(document.querySelector("#inp_custom_5").value);
            const ipNm3 = parseFloat(document.querySelector("#inp_custom_6").value);
            const birimRpTelAd = parseFloat(document.querySelector("#inp_custom_7").value);

            const result = ((rpTelAd1 * ipNm1) + (rpTelAd2 * ipNm2) + (rpTelAd3 * ipNm3)) / birimRpTelAd;
            document.getElementById("ret_custom").innerText = result;
        }
        document.getElementById("res_custom").addEventListener("click", calcCustom);
    </script>
