    <script defer>
        function calc56(params) {
            const src1 = parseFloat(document.querySelector("#inp_56_1").value);
            const src2 = parseFloat(document.querySelector("#inp_56_2").value);
            const dc = parseFloat(document.querySelector("#inp_56_3").value);
            const na = parseFloat(document.querySelector("#inp_56_4").value);
            const da = parseFloat(document.querySelector("#inp_56_5").value);

            const result = (10 * src1) / ((src2 * dc) + (na - da));
            document.getElementById("ret_56").innerText = result;
        }
        document.getElementById("res_56").addEventListener("click", calc56);
    </script>
