                    <mn>9000</mn>
                    <mo>x</mo>
                    <mn>1000</mn>
                </mrow>
            </mfrac>
        </math>
        <button type="button" id="res6">=</button>
        <div id="ret6"></div>

    </div>

    <script defer>
        function calc6(params) {
            const te = parseFloat(document.querySelector("#inp6_1_1").value);
            const atkiAd = parseFloat(document.querySelector("#inp6_1_2").value);
            const hamU = parseFloat(document.querySelector("#inp6_1_3").value);
            const td = parseFloat(document.querySelector("#inp6_1_4").value);

            const result = (te * atkiAd * hamU * td) / (9000 * 1000);
            document.getElementById("ret6").innerText = result;
        }
        document.getElementById("res6").addEventListener("click", calc6);
    </script>
